dget.show();                widget.not(':eq('+(current++)+')').hide();                setProgress(current);            }        }        hideButtons(current);    });    $.validator.addMethod("uploadFile", function (val, element) {        if(jQuery.type(element.files[0]) != 'undefined'){            var size = element.files[0].size;            if (size > 2097152){                return false;            } else {                return true;            }        }else{            return true;        }    }, "File size must be less than 2 MB");    // USer login validation & submission    $("#frmAddChild").validate({        ignore:":not(:visible)",        rules: {            parent_name  : "required",            parent_phone : "required",            parent_email : {required : true, email:true},            secondary_parent_email : {required : false, email:true},            primary_address : "required",            city  : "required",            state_id  : "required",            zipcode  : "required",            emergency_contact1  : "required",            emergency_contact2  : "required",            imgfile: {                required:true,                extension: "png|jpg|jpeg|bmp|gif",                uploadFile:true            },            guardian_name : "required",            guardian_phone : "required",            guardian_address : "required",            location : "required",            physician_name : "required",            physician_phone : "required",            insurance_card_front: {                required:true,                extension: "png|jpg|jpeg|bmp|gif",                uploadFile:true            },            insurance_card_back: {                required:true,                extension: "png|jpg|jpeg|bmp|gif",                uploadFile:true            },            insurance_carrier : "required",            group_number : "required",            first_name  : "required",            last_name : "required",            wear_protective_gear : "required",            under_supervision : "required",            foul_language : "required",            guard_name : "required",            cmprname: "required",            camper_rule_terms : "required",            camper_rule_terms2 : "required",            wear_helmet : "required",            under_supervision : "required",            running_terms : "required",            deny_access : "required",            risk_acknowledgement: "required",            minor_name: "required",            guardian2: "required",            accept_climbing_term: "required",            minor_name2: "required",            guardian3: "required",            ayfterm2: "required",            terms_airsoft : "required",            terms_airsoft : "required",        },        messages: {            camper_rule_terms : "Please accept the camper terms and services",            wear_helmet : "Please accept the terms to wear the helmet",            under_supervision : "Please accept the terms",            running_terms : "Please accept the terms",            deny_access : "Please accept the terms",            terms_airsoft : "Please accept the terms",            terms_airsoft : "Please accept the terms"        }    });});    // Change progress bar action    setProgress = function(currstep){        var percent = parseFloat(100 / widget.length) * currstep;        percent = percent.toFixed();        $(".progress-bar").css("width",percent+"%").html(percent+"%");          }    // Hide buttons according to the current step    hideButtons = function(current){        var limit = parseInt(widget.length);         $(".action").hide();        if(current < limit) btnnext.show();        if(current > 1) btnback.show();        if (current == limit) {             // Show entered values            $(".display label:not(.control-label)").each(function(){                console.log($(this).find("label:not(.control-label)").html($("#"+$(this).data("id")).val()));               });            btnnext.hide();             btnsubmit.show();        }    }</script><?php include(LIB_HTML . 'footer.php');?>