             required: true            },            address: {                required: true            },            state_id: {                required: true            },            city: {                required: true            },            zipcode: {                required: true            }        }    });    $('#tblCampHistory').dataTable({        "sDom": "<'row-fluid'<'span6'l><'span6'f>r>t<'row-fluid'<'span6'i><'span6'p>>",        "order": [[ 4, "desc" ]],        "aoColumnDefs": [            { 'bSortable': false, 'aTargets': [ 5 ] }        ]    });    $(document).on("click", ".btnViewCamp", function (e) {        var rid = $(this).attr("rel");        if(rid !== ""){            $.ajax({                url:'<?php echo AJAX_PATH;?>getCampRegisterDetails.php',                type:"get",                data:{rid:rid},                dataType: "html",                success:function(data){                    $('#tr'+rid).after(data);                    $("#btn"+rid).removeClass("btnViewCamp");                    $("#btn"+rid).addClass("btnHideCamp");                    $("#btn"+rid).text("Hide Details");                }            });        }    });    $(document).on("click", ".btnHideCamp", function (e) {        var rid = $(this).attr("rel");        if(rid !== ""){            $("#crow"+rid).closest('tr').remove();            $("#btn"+rid).removeClass("btnHideCamp");            $("#btn"+rid).addClass("btnViewCamp");            $("#btn"+rid).text("Show Details");        }    });});</script><?php include(LIB_HTML . 'footer.php');?>